"""Helper for loading static HTML assets."""

from __future__ import annotations

from pathlib import Path


def load_index(path: Path | None = None) -> str:
    """Load the static index HTML content."""
    target = path or Path(__file__).resolve().parents[2] / "frontend" / "index.html"
    return target.read_text(encoding="utf-8")


if __name__ == "__main__":
    print(load_index())
